INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6957, 'Знак  Стоп-линия  перед светофором по улице Пролетарская (остановка  Ж/д больница ) установлен очень низко. Зимой его не будет видно из-за сугробов. Исправьте.', null, 1);
