INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6963, 'Дорожный знак почти упал.', null, 1);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6964, 'Орск , Вокзальное шоссе 1 - На пешеходном тротуаре , возле дома Вокзальное шоссе 1 , лежат трубы отопления пересекающую этот тротуар . Этот тротуар не освещается и в темное время там можно  убиться  спотыкнувшись об эти трубы . В светлое время там приходиться перешагивать или перепрыгивать  , велосипедистам и  инвалидам в колясках там не проехать .', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6965, 'На ул. Дружбы и ул.Брестской делается ремонт вполне хороших тротуаров, а до светофора дорожку так и оставили в ужасном состоянии.  Торчат штыри и арматура.  Дети ходят в лицей через неё.  В дни, когда идут дожди, там не возможно пройти. Вчера я упала и повредила колено. Просьба принять меры и исправить проблему.', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6966, 'На каком основании частное лицо перекрыло муниципальную дорогу? Тем более само название улицы гласит само за себя "ул.Проезжая".', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6967, 'Перед регулируемым пешеходным переходом через проезжую часть по улице Пролетарская (остановка  Ж/д больница ) торчит арматура. Опасность травмирования пешеходов. Требуется срочно её демонтировать.', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6968, 'повреждение элементов сцены.', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6969, 'Добрый день!
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6970, 'Множественные ямы на дороге', null, 11);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6971, 'Дорожное покрытие двора испорчено ещё в 20 году, однако никак не подвергся ремонту. Также на ул.Цветной бульвар разрушилось часть дорожного полотна из-за его плохого качества. Из-за этого рядом со стадионом затруднено движение и парковка машин', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6972, 'Год тому назад проводилась замена труб на тротуаре вдоль дома №28. Траншею закопали и успешно забыли. Земля проседает, весной и после дождей летом грязь. И всё это в центре Орска, напротив Городской администрации. Стыдно за свой родной город!!!', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6973, 'здравствуйте, после ремонтных работ теплосетей если не ошибаюсь оставили бардак,а ведь начнутся дожди будет грязь, слякоть', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6974, 'В рамках капитального ремонта улицы Пролетарская (от ул. Ноябрьская до ул. Шевченко) не был благоустроен подход к пешеходному переходу через проезжую часть по улице Пролетарская напротив ТЦ  Берёзки . Прошу устранить данное замечание в кратчайший срок!', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6975, 'Ямы в дороге', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6976, 'Дорога по ул Аксакова от ул Спортивной и до пер Осенний вся в ямах .После дождя пройти по улице очень затруднительно , дети вынуждены идти в школу по другим улицам.  Когда вызываем такси не один таксист сюда не хочет ехать  .Просим вас произвести ремонт дорожного полотна', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6977, 'Прошу вас отремонтировать въезд во дворы домов 8, 10, 10/1 со стороны ул. Высотной.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6978, 'По улице Аксакова дорога требует ремонта . От ул Спортивной до переулка Осенний . Просим вас , так как наши дети каждый день ходят по этой дороге домой со школы , а если прошел дождь им приходится обходить по другим улицам', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6979, 'Разбитый тротуар между домами Салмышская, 14 и Юных Ленинцев, 18 - выход на перекресток и сквер Салмышского Боя', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6980, 'Здравствуйте. На пересечении ул. Транспортной и пр. Победы(в районе жк ласточкино гнездо), около светофора, глубокая яма. Не могу с июня 2021 ничего добиться, только все принимают заявки и ничего не исполняют!!!!! Скоро осень, все будут попадать в яму. Убедительная просьба, заделайте яму.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7036, 'По проспекту Гагарина 44/3 выбоины на тратуаре повсеместно, тяжело гулять с детьми или коляской.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6981, 'Здравствуйте. Уже много лет жители у домов Лабужского 2а и улицы Народной борятся с администрацией по благоустройству дороги соединяющей улицу Лабужского с улицей Новой. 
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6982, 'здравствуйте, на пешеходном переходе в районе улицы брестской который находится не по далеко от остановки родимцева, разбитый,ходить не удобно,не приятно', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6983, 'провалился асфальт,перекресток Зиновьев,пер.Грабовский уже с весны', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6984, 'здравствуйте, делали тротуар,но не доделали дорожку..', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6985, 'Добрый день! Прошу организовать адекватные переправы через коммуникационные рвы до зданий общего пользования', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6986, '3 недели разрушено покрытие. 
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6987, 'Ямы, отремонтируйте, пожалуйста, дорогу.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6988, 'На участке дороги по ул. НОВАЯ при движении с ул. Башиловых не доезжая до трк гулливер, на левом повороте  съезд к автомойке -  большая яма. Просим заделать.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6989, 'Пропасть, провал, возле Дома Б.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6990, 'Разбитый участок дороги 100 м.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6991, 'Асфальтное покрытие в городе Орск от проспекта Мира 2 до ул.Металлистов 3 находится в не нормативном состоянии на протяжении 5 лет.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6992, 'Прошу проверить состояние автодороги посёлок Ленина ул. Дорожная на соответствие требованиям ГОСТ P50597-93', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6993, 'Огромная дыра в асфальте, ограждение ее не закрывает, внизу лежат палки с торчащими гвоздями. В связи с тем, что вокруг находятся одни стройки, попасть в детский сад №19 корп. 2 возможно только с улицы Саморядова, то есть люди с маленькими детьми постоянно проходят мимо этой ямы и могут по неосторожности упасть вниз. Прошу закрыть данную яму и сделать возможным проход к детскому саду №19 корп.2 не только с данной улицы', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6994, 'тротуар бани на тагильской 39. не ровно положили асфальт люди спатыкаются и часто падают', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6995, 'Добрый день. По адресу ул. Гагарина, 35 (дорога между домом и д/с №200) глубокие ямы и выбоины, затрудняют движение автомобилей.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6996, 'Здравствуйте! Жители города Бугуруслана обращаются с просьбой отремонтировать дорогу по ул. Соловьиная, и где пересекается с ул. Тульская, так как дорога находится в неудовлетворительном состоянии. На нашей улице проживает очень много детей, которые ходят как в школу, так и в детский сад, но по этой дороге невозможно ходить, так как отсутствует твёрдое покрытие, особенно в весенне-осенний период во время распутицы. В этот период времени ни скорая помощь, ни пожарная не могут проехать по этой дороге. Очень просим Вас помочь в решении данной проблемы.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6997, 'Просел грунт и образовалась яма на тротуаре со стороны проспекта у дома 39 по пр. Ленина в г. Орске.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6998, 'Дорога в ужасном состоянии,не проехать не пройти.Очень просим о помощи в разрешении данного обращения.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (6999, 'На тротуаре возле входа в СК "Олимпийский" плитка разрушилась и провалилась, ходить стало опасно, особенно для пенсионеров. Прошу отремонтировать данный участок тротуара.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7000, 'Уважаемые господа! Обращаются к Вам за помощью жильцы многоквартирных  домов Туркестанская 39/3, 39/1. Во время ремонта улицы Туркестанской весь поток автомобилей проходил по нашему двору, который для этого не предназначен,построен был в далекие 60е годы,нет даже тротуара для пешеходов а дом находится между двумя детскими садиками № 33 и № 133. В результате интенсивной эксплуатации участок дороги возле домов и участок дороги с ул.Уральской д лицея № 5 длиной 50-70м пришел в аварийное состояние, по которому проходить не безопасно, особенно родителям с детьми. Просим Вас оказать содействие в решении данного вопроса.
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7001, 'Дорога находится в очень плохом состоянии, как минимум два года нету никакого ремонта дорожного полотна. Много ям, выбоин.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7002, 'В нашем районе вокзала в Старом городе отремонтировали много улиц - спасибо огромное! Но перекрёсток ул. Вокзальной и Вокзального шоссе остался отремонтированным НАПОЛОВИНУ! 
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7003, 'Прошу принять меры и восстановить дорожное полотно, которое было значительно повреждено при строительстве дороги на улице Уральской в 2019 году. 
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7004, 'Огромная яма на дороге, которую никак не объехать, не обойти. Из года в год она, уже много лет на этом месте.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7005, 'Здравствуйте! Месяц назад на ул. Степана Разина демонтировали бордюры, разрушили тротуары, при этом сделав невозможным заезд на автомобилях во дворы частных домов. За этот месяц никаких работ исполнитель ремонтных работ не вёл и не ведёт по состоянию на сегодня. Кто-нибудь контролирует работу подрядчика? Есть вообще график работ или подрядчик предоставлен сам себе? Зачем нужно было срочно выковыривать бордюры, если никто их укладывать пока не собирается? Вы хотите повторить прошлогоднюю ситуацию с Туркестанской?', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7006, 'Б - безбарьерная среда по оренбургски. Перекресток Новой и Пролетарской около трк Гулливер. Не доделали тольуар и решили, что и так сойдет. Подумаешь нагрузка на ГКБ 4 немного увеличится.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7007, 'на улице карагандинской рядом с трк сокол коммунальная служба латала провал под асфальтом и оставили после себя ямку засыпав её песком, песок вымыть дождями и теперь машины ломают себе подвеску. прошу заасфальтировать эту полоску', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7008, 'Здравствуйте! Хотелось бы обратить внимание на отвратительное состояние покрытия на дороге на больничном проезде 10 между домами 8 и 8а. Такая ситуация уже более 7 лет. Дорога является подъездом к областной детской стоматологии и естественно каждый день десятки машин. Неоднократно тут пробивали колёса и царапали низ машин. Прошу обратить внимание и исправить данный участок дороги', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7009, 'Здравствуйте, в Орске на против дома по ул.Сорокина 22, на дорожном покрытии яма. Прошу помочь в решении этой проблемы. Заранее спасибо.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7010, 'Добрый день!Проблема заключается в том, что по этой лестнице опасно спускаться в подниматься. Там ежедневно ходят работающие люди, и пенсионеры, и скоро дети пойдут в школу, и сады.  Другого варианта (спуска-подъема)просто нет. По дороге грязь, ямы , это единственный вариант как добраться до города быстрее пешком. А что будет, когда погода испортится, когда гололед? Помогите, пожалуйста, отремонтировать ступеньки.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7011, 'На дороге, ведущей к дому 9А по ул. Пацаева в г. Орске глубокие ямы.', null, 12);
