INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7013, 'Здравствуйте, подскажите пожалуйста, когда будут произведены работы по устранению ям на пешеходном переходе перекрёстка улиц Родимцева и Брестской, в районе остановки Родимцева. Сам пешеходный переход не оборудован, нет спуска с тротуара. Так теперь и ямы разрослись, что практически не возможно по нему ходить.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7014, 'Тротуарная плитка сильно повреждена и создает опасность для  граждан. В целом как и повреждения ступенек в подземном переходе', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7015, 'Здравствуйте, за домом по адресу ул.Комарова 20.  на дорожном покрытии ямы. Прошу помочь решить данную проблему. Спасибо.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7037, 'Тротуар не доделали, бросили, работы больше не ведутся на этом месте. Зачем сняли ровное хорошее покрытие, чтоб мы ноги ломали по камням?', null, 12);
