INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7016, 'При подготовке к работам «Росводоканал Оренбург» по прокладке трубопровода методом горизонтально-наклонного бурения по ул. Ваана-Теряна от ул. Одесской до ул. Кардонная, проводились работы по вскрытию участков дорожного покрытия по выявлению пересечений с другими коммуникациями. На участке дорожного покрытия по ул. Ваана-Теряна между домами 22 и 24 проводилось вскрытие полотна дороги, после чего участок был засыпан. Но при работах по восстановлению дорожного полотна участок не был отремонтирован. В соответствии с Постановлением администрации г. Оренбурга №2240-п от 12.09.2012 г. Должны быть выполнены работы согласно п. 3.6 данного Постановления.', null, 12);
