INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7033, 'Проблема возникла в связи с переносом остановки МЖК. остановку перенесли, карман сделали. Но теперь попасть с этой остановки на ул. Конституции и Березки будет невозможно. Раньше рядом с регулируемым переходом была асфальтированная дорожка к улицам конституции и Березки. Теперь эта дорожка оказалась за остановкой. Новой дорожки не сделали и не уже ремонт закончен. В итоге, хотели как лучше, а получилось, что остановка МЖК доступна теперь только для жителей МЖК. У нас в доме Березки 2/5 проживают два инвалида по зрению, мамочки с колясками куда? Только в грязь. Причём те, кто ремонтировали ул. Конституции подумали об этом тротуаре, и сделали заход на дорожку. но хозяина в городе нет. Одного.', null, 12);
