INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7110, 'Просим привести дорожное полотно в надлежащий вид, отвечающий безопасности движения, устранить ямы выбоины, так как глубина ям превышает допустимые нормы, так же поставить на кадастровый учет автодорогу на участке за домом № 1 в 3 микрорайоне и внести автодорогу в систему улично- дорожной сети г. Бугуруслана для обслуживания данного участка автодороги в зимний и летний период эксплуатации.', null, 12);
