INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7146, 'Провал дороги на Матросском Краснознаменной по переходной дорожке', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7147, 'Уважаемые руководители города Оренбург! Обращаю Ваше внимание о плохой дороге по улице Калинина 1-9. По ней ежедневно ходят дети с ограниченными возможностями в школу. в 2020 году проблема озвучивалась, письмо от Вас поступало что в план поставили, а воз и ныне там. Пешеходный переход есть без ограничителей скорости, Сделали стоянку и то до конца не благоустроили ( не заасфальтировали), пыль летом, осенью грязь будет. Прошу принять меры по исправлению недопустимой ситуации, когда дети с ограниченными возможностями, да и простой народ сможет ходить, ездить по нормальной дороге?', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7148, 'Тротуар по улице Алтайская от дома 2/1 до Геннадия Донковцева, 5 давно пришел в негодность. на некоторых участках его вообще нет. в осенне-зимний перед земля размокает и превращается в жижу. зимой там горка по которой опасно ходить.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7149, 'Летом отремонтирована ул Пушкинская есть гарантийный ремонт по какой причине провалы…и въезд в парк  уробилин совсем.', null, 12);
