INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7170, 'Добрый день. Множество ям,по ул.Маврицкого которые постоянно образуются особенно после зимы.100м дороги между ул.Чичерена и ул.Яицкая .', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7171, 'На участке дороги от Чкалова 39 до Чкалова 39/1 (по дороге-спуску от Европы-Азии) ямы, выбоины на дороге.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7172, 'Добрый день. После ремонта дороги на улице Краснознаменной.не заделаны асфальтом тротуары примыкающие к проезжей части.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7173, 'Данная дорога в аварийном состоянии уже много лет, все управляющие которые находятся на территории района говорят что этот участок дороги в нашем районе к ним не относится и поэтому ее некто не ремонтирует, и вообще дороги в мкр 70 лет ВЛКСМ оставляют желать лучшего, кое где насыпали камней в ямы , но камни это не асфальт и они уже давно выбились из ям. Просто может кто  то повлияет на эту ситуацию, если проехать в круг по району то будет просто в ужасе от дорог и машину жалко всю подвеску можно разбить. и это малая доля что можно выложить, если бы можно было бы больше фото добавлять я бы добавила.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7174, 'Очень плохое состояние асфальта по переулку Станочному. Много глубоких ям, убитые обочины. 
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7175, 'На выезде с пер. Канарейкина на ул. Абдрашитова расположены 4 крупные ямы, как минимум одна из них представляет явную опасность для конструкции автомобиля', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7176, 'На улице Пролетарской выполнен ремонт дороги, за исключением участка, где расположен железнодорожный путь,который не эксплуатируется болеем 20 лет. Также хочу обратить ваше внимание, что именно на данном участке каждую весну образуются глубокие ямы , что сильно затрудняет проезд. Прошу Вас принять меры по устранению данной проблемы.', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7177, 'возле перехода на брестской тоже не очень хорошо выглядит полотно,все убито,не красиво', null, 12);
