INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7179, 'Въезд на пер.Почтовый к дому №11 с улицы 8 марта  имеет многочисленные повреждения дорожного полотна ямы большие и глубокие за что платим налоги в казну если у машин колеса отлетают?', null, 12);
