INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7181, 'Добрый день.ямы на дороге, также просевший асфальт,после дождя стоит большая лужа ,годами .', null, 12);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7182, 'Постоянно после дождя скапливается большая лужа перед выходом с ул мира на ул Спортивную через каторую пешком невозможно пройти', null, 14);
