INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7184, 'Постоянно во время дождя и весной при таянии снега подтопляет пешеходный переход и подход к  нему у дома 57 по пр. Ленина в г. Орске на пересечении с ул. Нефтяников.', null, 14);
