INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7187, 'Подход к пешеходному переходу постоянно подтопляет во время дождя или таяния снега на ост. ул. Тбилисская со стороны дома 94 по пр. Ленина в г. Орске. К переходу невозможно подойти, тк уровень перехода выше, чем тротуар перед ним. Уровень тротуара необходимо сровнять с уровнем перехода.', null, 14);
