INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7197, 'Здравствуйте. Примите меры, мы тонем. Воду не откачивают, машины застревает, администрация северного округа и города Оренбурга не реагирует, дорогу в план ремонта в этом году опять не включили, я 3 года подряд у усдх выпрашиваю ремонт дублер Победы, дорога к дому Победы 170 и 168 в ужасном состоянии.Никому нет никакого дела. заместители главы администрации северного округа Сухенко и Давыденко на компромисс не идут... На встречу к ним попасть не реально', null, 14);
