INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7201, 'Здравствуйте. Проблема нескольких десятилетий именно этого пешеходного перехода. Даже после небольших дождей края перехода ( от 1.5 до 2 метров летом, и от 2.5 до 3 метров в весеннее таяние снега) подтопляются  и  на переход возможно зайти: с нечётной стороны от перекрёстка ул. Ногина ( 15 - 20 метров), с четной стороны за 8 -12 метров слева от перехода. Недавно  на этом переходе установили светофор " по требованию", т.е. с кнопками и получится так, что нажав кнопку, пока пешеход обойдёт лужу и вступит на пешеходный переход уже загорится сигнал запрета перехода. Пешеход просто не будет успевать перейти проспект. Что уж говорить о медленных стариках.... Оптимальный вариант- приподнять переход', null, 14);
