INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7269, 'На пересечении улиц Джангильдина и Загородного шоссе при повороте на лево из- за дорожного знака не видно встречного потока,этот участок до этого был проблемным,а теперь ещё и знак мешает.Просим установить на этом перекрестке светофор или убрать этот знак.', null, 2);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7270, 'При въезде в жилую зону от проспекта Победы (вблизи дома 160) знак «жилая зона» закрыт деревьями. Для лучшего информирования водителей и повышения безопасности движения нужно установить знаки с двух сторон проезжей части.
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7271, 'На въезде на перекресток по ул. Фадеева перед ул. Пролетарская стоит знак "Движение прямо и направо", висит табличка "Объезд" со стрелкой, указывающей прямо. Но если двигаться прямо по ул. Фадеева, сразу же после пересечения ул. Пролетарская стоит знак "Движение запрещено". Закройте, пожалуйста, этот знак. Сбивает с толку.', null, 2);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7272, 'После ремонта дороги по улице Сахалинской, на повороте в частный сектор по переулку Дальнему была нанесена новая разметка, а именно двойная сплошная линия. Из-за чего теперь невозможен поворот на переулок при движении "из центра". Всегда здесь была прерывистая линия.', null, 2);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7273, 'Как видно на фото, дорожный знак "Пешеходный переход" дублируется только на одной стороне проезжей части по улице Пролетарская.', null, 2);
