INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7286, 'На одном из самых оживлённых перекрёстков города на пересечении улиц Новая и Пролетарская возле ТРК "Гулливер" отсутствуют обозначенные дорожными знаками и разметкой пешеходные переходы. Огромный поток людей переходят здесь дорогу. В целях безопасности дорожного движения и для устранения данного несоответствия необходимо оборудовать данный перекрёсток пешеходными переходами согласно требованиям ПДД РФ.', null, 2);
