INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7292, 'Здравствуйте! У нового надземного пешеходного перехода по Нежинскому шоссе в районе пос. Пригородного отсутствует пандус. Совершать подъем и спуск по лестницам данного сооружения крайне затруднительно с детской коляской, велосипедом. У перехода имеется лифт для маломобильных граждан, который не предназначен для подъема с велосипедом. Прошу установить пандусы на всех надземных пешеходных переходах по Нежинскому шоссе. Спасибо!', null, 21);
