INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7367, 'Пешеходный мост через Елшанку в районе 6 школы , возле реки помойка , прошу навести порядок , это место где гуляют мамы с детьми', null, 24);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7368, 'Добрый день! На пересечении переулка Мельничный и ул. Некрасова есть овраг где растут деревья. В нём постоянно много мусора, деревья разрослись и задевают газовые трубы и электрические столбы. Никогда там не убираются поселковые службы. Там сухая трава, которая может привести к пожара. Просьба привести в порядок данную территорию. Давайте заботиться об экологии и безопасности!', null, 24);
