INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7387, 'Мусорная площадка, расположенная на пересечении улиц Фрунзе и Пролетарская находится в ужасном состоянии, давно пришла в негодность. Ограждение и баки разрушены, из баков мусор разлетается по всей улице и на территории домовладений. У населения отсутствует возможность раздельного сбора мусора. Кроме того, вокруг баков постоянно складируется крупногабаритный, строительный и другой мусор. Поскольку нет организованной площадки в эту свалку постоянно сбрасывается отработанная компьютерная техника, что совершенно небезопасно! Бездомные животные практически живут в этой территории, растаскивая мусор. Установите, пожалуйста, организованную площадку!', null, 24);
