INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7437, 'Возле домов №11 и №13 по улице Зиминской растут необрезанные деревья, которые разрослись настолько, что мешают проезду автомобилей (в т.ч., при необходимости, медицинских и пожарных служб), задевают ветками за провода проходящей рядом ЛЭП, повышают риск обрыва проводов и поражения, вследствие этого, электрическим током. Деревья требуют подрезки до размера, не представляющего опасности для проезжающего транспорта и линий электропередач.', null, 35);
