INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7457, 'На тратуаре по ул. Уральской на пути в жк дубки не убирается снег. Снега по колено!Приходится ходить по проезжей части и мимо гаражей, недавно там напала собака. Примите, пожалуйста, меры, дорога не самая простая и короткая, общественный транспорт не ходит. Многие пешком добираются до остановки на ул. Чкалова, мамы с колясками ведут в сад,дети идут в школу, лицей.', null, 39);
