INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7459, 'Мой адрес Прлетарская 121. На карте не нахожу. При уборке снего 02.02.2121 в 06.40 спецтехникой сгребли сугроб вместе с кустом сирени, Задвинули его на на прочищённый выезд из моего гаража. Сугроб не мешал никому. Ни пешеходам, ни транспорту. А теперь мешает и пешеходам и перекрыл выезд из гаража. Особенно меня бесит то, что эта проблема повторяется регулярно. Т.е. регулярно при вывозе снега, его вывозят вместе с насаждениями. Саженцы не успевают вырасти. Пирамидки ограждение не спасают, т.к. их перекрывают сугробы. Замкнутый круг. На все мои телефонные звонки в администрацию, меня футболят по кругу от одного к другому и никакого результата. Ответственных нет и наверно не будет. Наболело', null, 39);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7460, 'Прошу обеспечить уборку снега на тротуаре вдоль улицы Пролетарской от остановки Ноябрьской до АЗС EXOIL (по направлению движения в центр)', null, 39);
