INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7462, 'Неубранный снег на проезжей части и тротуарах в районе, ограниченном улицами Томилинская, дама 239-245 и ул. Планерная, дома 1-12. Тротуары также завалены снегом, школьники вынуждены идти по проезжей части - угроза жизни и здоровью.', null, 39);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7463, 'Прошу обеспечить уборку снега на тротуаре (на всем протяжении) вдоль улицы Сахалинской между улицей Саракташской и переулком Дальним (по направлению движения в центр). Дополнительно отмечу, что спец.техника регулярно проезжает по тротуару, при этом не сгребая снег, что можно понять по углублённому следу от колес техники. Считаю недопустимым такие бесполезные выезды техники, при которых создается лишь видимость выполнения работ по уборке снега.', null, 39);
