INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7473, 'В связи с несвоевременной очисткой тротуара от снега после повышения температуры на тротуаре образовался гололёд. Прошу обеспечить уборку слоя снега и льда с использованием реагентов на тротуаре между улицей Саракташской и переулком Дальним.', null, 39);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7474, 'Не вывозят снег с центра города Бугуруслана по улице Революционной от 64 по 70 дом, снег вдоль дороги, в 2020 году на данном участке производили ремонт дороги с установкой бардюров, по нормам содержания дорог на участках где установленны бардюры в зимний период должны вывозить снег.', null, 39);
