INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7488, 'По ул. Карпова не чистят снег. Участок дороги ул. Карпова от перекрестка с ул. Салмышской до дома № 67/1 завален снегом, заужена проезжая часть. Дорога с обоих сторон заужена на более чем в 2а метра от края фактического расположения обочены . На данном участке дороги интенсивное движение: въезд к жилам домам 19го микрорайона и въезд к торговому комплексу "КИТ", автомобили с трудом разъезжаются. Прошу очистить проезжую часть от снега.', null, 39);
