INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7580, 'Добрый день. По адресу пр. Гагарина 41/2 во дворе возле дома глубокие ямы и выбоины. Затрудняют движение автомобилей.', null, 13);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7581, 'во дворе, в асфальтовом покрытии у первого подъезда образовалась яма. Управляющая компания "Северная" отвечает, что это не их территория, и помочь жителям дома залить яму они не могут. На вопрос кому же принадлежит этот участок дороги, они ответить затрудняются. Тротуаров в нашем дворе нет. Все, и жители и автомобилисты пользуются этим выходом со двора. А между тем, яма становиться все больше. Возможно ли отремонтировать этот участок? Жильцы дома исправно оплачивают коммунальные услуги, в которые входит много разных услуг. Просим Вас поручить ответственным за этот промежуток дороги ремонт участка. Фото прилагается.', null, 13);
