INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7604, 'Вскрыли асфальт 14.09.21 для проведения ремонтных работ. На протяжении 4 дней никаких ремонтных работ не производилось. Безответственности рабочих и должностных лиц. Фото от 18. 09.21. Дворовая территория Промысловый проезд 6/1', null, 13);
