INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7587, '"Благоустройство"  придомовой территории ул. Всесоюзная 3 в рамках "НАЦИОНАЛЬНОГО ПРОЕКТА!!!!    Издевательство над жителями дома за их же денежные средства.   Просим вмещаться в ситуацию,  грядут выборы,  может быть кто то отреагирует и  решит это безобразие. Нас лишили детской площадки, зеленых насаждений и все это под лозунгом БЛАГОУСТРОЙСТВО. во дворе просто опасно находиться и это перед 1 сентября, когда масса детей вернулась с отдыха!  Где наши депутаты? Господин Ильиных пробежал в начале "БЛАГОУСТРОЙСТВА" мелькнули в СМИ  - отчитались что все контролируют, за державу обидно! Стыдно ...за такие деяния  и виновных нет,  вот это  "КОМФОРТНАЯ ГОРОДСКАЯ СРЕДА" !', null, 13);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7588, 'Ленинская 57 А. Росводоканал проводил по осени 2020 года работы по канализации,в результате был испорчен хороший асфальт во дворе.Работники заложили свои раскопки тротуарной плиткой и сказали с наступлением лета 2021 положим асфальт,а воз и ныне там.', null, 13);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7589, '18 мая 2021 года мною на дороге  располагающейся  возле многоквартирного дома по адресу г.Оренбург ул. Туркестанская 57(слева и за домом) обнаружено повреждение дороги,ямы ,выбоины, размеры которых превышали установленные п.5.2.4 ГОСТ Р 50597-2017 ,что создает опасность и угрозу дорожному движению.В соответствии с п.42 Постановления Правительства РФ №491 от 13.08.2006,за ремонт общедомового имущества,включая дорожное полотно,входящую в придомовую территорию отвечает управляющая компания
