INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7629, 'Просим организовать наличие подъездных путей для экстренных служб к подъездам у дому Оренбургская область г. Бугуруслан 3 микрорайон д. 1, а именно наличие автодороги на землях госсобственности возле дома №1 3 микрорайона, установлено, что дом №1 на землях госсобственности есть, а дороги и подъездных путей к подъездам жильцов нет, дорога не является объектом улично-дородной сети г. Бугуруслана. Просим ликвидировать яму возле дома №1 3 микрорайона, привести на данном участке дорожное полотно в надлежащий вид, отвечающий безопасности движения, поставить на кадастровый учет автодорогу вокруг дома.', null, 13);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7630, 'уже не первый год подряд, напротив административного здания мясокомбината, прямо у тротуара расположен открытый канализационный коллектор с воткнутым деревом вместо крышки. Тут расположена парковка, много людей, бегают дети, мы ждём несчастного случая чтобы решить эту проблему?', null, 16);
