INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7637, 'Здравствуйте. По адресу п.Новосергиевка ул Ленинская между домами 92 и 90, расположен люк. Каждый год там ставят три палочки и вешают красную ленточку. Зимой трактор чистит снег и естественно данная конструкция ломается. Летом часто туда проваливаются машины, рядом живут дети. На днях вызвали скорую помощь, при развороте она угодила передним колесом в эту яму, яма была закидана жестянкой и досками. Как итог, пришлось самостоятельно добираться до приемного покоя, потому что времени ждать вторую скорую не было. Помогите решить данную проблему. Ведь там живут и дети, которые могут туда угодить.', null, 16);
