INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7644, 'Посреди дороги просел канализационный люк, объезжая который создаются помехи для проезда встречного автотранспорта, чем провоцируются ДТП.', null, 16);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7645, 'Добрый день. На проезжей части уже несколько дней открыт люк колодца. Водители сами поначалу помещали туда длинные ветви деревьев для отметки опасного объекта на дороге. Сейчас ничего нет. Крышка отсутствовала. Сейчас появилась, но люк открыт, это может стать причиной ДТП, особенно в ночное время.Люк вечером виден плохо.', null, 16);
