INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7647, 'Здравствуйте ! Жители дома по у.Комсомольской 107 г.Бугуруслана возмущены безхозяйственному отношению к люку, наполненному водой, на протяжении длительного времени, со стороны ООО" Тепловодоснабжение".( 9 октября в Ваш адрес было направлено  письмо  об открытом люке и о том ,что сломан гидрант и люк наполнен водой)Был получен ответ, что все  исправлено, что не соответствует действительности.Гидрант не отремонтирован.Вода стоит в этом месте уже длительное время, опускается тротуар.А скоро придут морозы и стоячая вода в люке скажется на снабжении нашего дома холодной водой( замерзнет водопровод).Неужели к  разрешению данной  проблемы нужно  подключать  Губернатора  области ?????', null, 16);
