INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7660, 'Около двух месяцев назад проводилась обрезка деревьев. Спиленные ветки до сих пор лежат на том же месте. При этом машина по сбору крупногабаритного мусора приезжает и собирает мусор от организованной мусорки, но ветки рядом с ней игнорирует.', null, 25);
