INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7662, 'Добрый день…. Центральный вход в детский сад #200  пр.Гагарина 41/4 вход постоянно завален мусором идёт ужасный запах, мусор не убирают, ребёнок и родители затыкают нос чтобы пройти в детский садик, просим перенести мусорку в другое место, тк не соответсвует санитарным нормам  ….. прошу Вас принять меры!!!', null, 25);
