INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7664, 'Повторное обращение по ранее опубликованному на портале сообщению № 712 от 06.04.2021. К сожалению, не весь строймусор был убран с ветвей дерева перед домом № 255 по улице Пролетарская (со стороны главного фасада). На ветвях дерева на уровне 3 этажа 5 подъезда по-прежнему висит МЕТАЛЛИЧЕСКИЙ КРЮК С ПРОВОЛОКОЙ, который при сильном порыве ветра может разбить окно или упасть кому-нибудь на голову.', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7665, 'Ненадлежащее содержание придомовой территории дома номер 2 по ул.Братской - мусор, битое стекло, сваленные крупные ветки на тротуаре', null, 25);
