INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7673, 'Неделю назад РВК г. Орска устраняли аварию по адресу пер. Музыкальный  д. 12 и после ремонта оставили такую картину.', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7674, 'Добрый день. Ветки лежали с мая месяца. Огромная гора. Сегодня, видимо, чтоб не убирать, взяли всю кучу и подожгли. Стало не лучше.', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7675, 'В этом доме (фото 1) находится ЖЭУ-9 и УКП  ГО и ЧС. А вокруг здания на прилегающей территории творится полное безобразие.  Это и горы мусора ( фото 2 и фото 3) и проваленные остатки асфальтированных когда то дорожек (фото 4) и старые засохшие деревья ( фото 5). В таком состоянии территория находится уже много лет,', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7676, 'Не убирается территория вокруг контейнерной площадки для сбора мусора по адресу ул.Родимцева д.5 возле входа в д/с 156. Жалобы в Управляющую компанию Сармат, в диспетчерскую гОренбурга 30-40-40 результатов не дали. Помогите, пожалуйста, навести порядок.', null, 25);
