INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7688, 'Просим убрать кучу песка с торца дома 255 по улице Пролетарская, оставшуюся после завершения ремонтных работ на крыше.', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7689, 'На мусорной площадке установлено два контейнера для пищевых отходов и небольшая площадка для крупногабаритных отходов методом навала. Количество контейнеров на мусорной площадке устанавливались исходя из количества жителей близлежащих домов (6-ти этажных), но не учитывали частный сектор, дома которого примыкают к мусорной площадке. Даже при ежедневном вывозе отходов с площадки крупногабаритного мусора, она заполняется меньше, чем за сутки. Особо настораживает жителей, что в районе накопления отходов валом собирается стая собак, которые растаскивают мусор и представляют опасность для прохожих. Просим привести в порядок, согласно санитарным правилам и нормам и установить бункер-накопитель.', null, 25);
