INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7691, 'Просьба убрать мусор под деревом и на газоне рядом с парковкой во дворе  дома 255А по улице Пролетарская.', null, 25);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7692, 'Уже 2 года после того как были установлены новые мусорные баки и железное ограждение, старые бетонные плиты и горы земли так и небыли убраны.', null, 25);
