INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (7699, 'Около нашего дома помимо мусорных баков есть ограждение для крупногабаритного мусора. Мало того. что там постоянно накапливается мусор,  так ещё и многие жители считают. что могут туда выбрасывать всё,  что им вздумалось.  Так 1 ноября подъехал мужчина на газели и выбросил пластиковые 5литровки,  целлофан и тд. Рядом стоят баки мусорные, контейнеры для пластика и прочего сортировочного мусора, но он решил что можно мусор просто бросить в и так уже огромную кучу , которая накопилась практически за неделю...Госномер газели имеется и видео тоже Н048НО.  Куда нужно обратиться,  чтобы привлечь мужчину к ответственности. ???', null, 25);
