INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8199, 'Нескошенная трава возле дома и площадки ТБО. Всегда мусор возле площадки ТБО. Нет дороги к площадке ТБО,когда дождь к не не пройти не проехать.', null, 31);
