INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8218, 'Санитарная обрезка деревьев
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8219, 'Здравствуйте, в один из ветреных дней во дворе дома по переулку Бухарскому 21 сломало огромную ветку дерева, которое своей кроной разрушает штукатурку исторического строения, и эта ветка повисла над жителями дома, потому как там проходит тропинка к двум подъездам. Звонок в администрацию ничего не дал.', null, 37);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8220, 'В результате бездействия ук Братство, выразившегося в непроведении своевременной опиловки деревьев, крупная ветка упала на тротуар.', null, 37);
