INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8256, 'Добрый вечер! Прошу разобраться со сложившейся проблемой, а именно огромной горой снега на придомовой территории по ул.Чкалова, д.15 и д.17. Такая проблема каждый год, но в таком масштабе впервые. Эти горы образовались еще 3 дня назад перед снегопадом после расчистки двора. В ближайшие дни будет стоять теплая погода, дождь со снегом, и вероятнее всего наши дома скоро будут плавать, как и дома частного сектора за нами. Фото прилагаю.', null, 42);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8257, 'Здравствуйте, огромное количество снега столкнули когда чистили улицу зимой , сейчас все таил огромное количество вода она стоит , низкое место, если возможно вывезти снег.', null, 42);
