INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8260, 'Управляющая компания двор почистила от снегом, за это им конечно большое спасибо. Вот только теперь во дворе огромная лужа, её не обойти, ни машину припарковать невозможно, а все потому что выезд со двора так никем и не был очищен от снега.', null, 42);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8261, 'Снежные кучи, сформированные на въезде во дворы домов по ул. Восточная 1, 1а и 3', null, 42);
