INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8281, 'Куча грязного снега во дворе около детской площадки, которая занимает 2 парковочных места. Только увеличивается.', null, 42);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8282, 'Оренбургский колледж экономики и информатики не вывозит кучу снега. Данную кучу навалили прям на детскую площадку.', null, 42);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8283, 'Скопление снега и льда на крыше торгово-административного здания по адресу ул. Чкалова, д.53/2. Собственник здания игнорирует опасность для посетителей и прохожих, прошу принять меры воздействия для устранения угрозы жизни и здоровью.', null, 43);
