INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8297, 'Двор в ночное время крайне слабо освещен. Из окна дома совершенно ничего не видно: ребенка, идущего в школу (Лицей4), автомобиль, стоящий под окном. Фонари освещают только детскую площадку, которая в это время никому не нужна. Беспокоимся за безопасность детей и имущество. Не работает фонарь рядом с контейнерами для отходов (уже не один год). Спасибо.', null, 5);
