INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8299, 'Живу несколько лет в ЖК Времена года, а точнее пр-т. Победы 157/5 и за все время не видел чтоб работали уличные фонари. И это во всем районе ЖК Времена года. Прошу Вас разобраться в этом вопросе. Заранее спасибо.', null, 5);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8300, 'улица Южно-Уральская между Ленинградской и Д.Донскова не освещена совсем (дома с 14 по 24). в то же время один квартал в начале улицы и четыре квартала после Ленинградской имеют уличное освещение.', null, 5);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8301, 'Братьев Башиловых д. 15А не горит уличное освещение над первым подъездом уже на протяжении полугода. Неделю назад была подана заявка в обслуживающую организацию ООО «ОРЕНДОМ» но они до сих пор бездействуют.', null, 5);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8302, 'Парк на ткачева завален мусором, вода в фонтане грязная, зелёного цвета, дети купаются в этой воде. Фонтан неисправен. Прошу привести сквер на ткачева в нормальное состояние, хотя бы для начала убрать мусор.', null, 28);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8303, 'Увеличение выброшенного строительного мусора. Готов провести к месту свалки!', null, 28);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8304, 'Просим убрать тяжёлый и лёгкий мусор в лесопосадке возле старого кладбища', null, 28);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8305, 'Парк 50-летия влксм находится в ужасном состоянии, горы мусора, бомжи, стихийный рынок людей, торгующих хламом каждые выходные и оставляющие после себя горы мусора. Детей страшно на улицу выпустить! Администрация обратите внимание, расчистите и уберите мусор! Рядом прекрасный объект центр настольного тенниса, ухоженный, почему же рядом помойка?!', null, 28);
