INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8319, 'Прошу обязать собственника либо арендатора территории систематически убирать и поддерживать в надлежащем виде территорию лесополосы вдоль улицы Театральной прилегающей к парку 50-летия СССР и микрорайону 70 лет ВЛКСМ.  Мусор, засохшие ветки деревьев свисают прямо над дорожкой.', null, 28);
INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8320, 'Здравствуйте, в Мамалаевке до сих пор не скосили всю траву в парке и очень много мусора на всей территории. Никто не убирается. Конопля растёт целая плантация. Ограждение не установлено, хотя районная администрация отчиталась, что траву скосили.', null, 28);
