INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8459, 'Добрый день. Очень интересует вопрос, когда же починят фонари вдоль проспекта "Новый", которые освещают общежитие строительного колледжа (дом № 24) и проулок в торце дома № 16 в первом микрорайоне. Кроме большого потока детей, которые идут в школу № 3, в любое время суток оживленное движение машин.  В условиях непредсказуемой погоды - то гололед, то лужи по колено, из-за ям и неровностей дороги, а главное, отсутствия освещения, для людей становится опасно передвигаться на данном участке дороги. На основании вышеизложенного прошу Вас с вниманием отнестись к моему обращению и как можно скорее устранить неисправность освещения.', null, 4);
