INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8488, 'Добрый день. Возле дома по ул. Дмитрия Донского д. 4а не работает фонарь уличного освещения. Уже 2 года !!!! Скоро будет темнеть рано, а по данной дороге дети возвращаются со школы №5, местные жители ходят в магазин магнит. Тьма стоит непроглядная.  Прикладываю фото фонаря, чтоб точно обозначить его месторасположение. С надеждой на чудо, житель дома по ул. Сызранская/Дмитрия Донского 37/3.', null, 4);
