INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8512, 'Есть фонари, но они не работают! Обращались в УК «новый город» (раньше они освещали) но пояснили что это городская территория и они отключили подачу электроэнергий на столбы уличного освещения! Пожалуйста включите освещение за счёт бюджета! Жильцы домов и квартир освещают улицу за свой счёт! Ул проезд 4-ый', null, 4);
